import React from 'react';
import { connect } from 'react-redux';

import { toggleCartHiddenAct } from '../../redux/cart/cart.actions';
import { ReactComponent as ShoppingIcon } from '../../assets/shopping-bag.svg';
import './cart-icon.styles.scss';

const CartIcon = ({ toggleCartHidden }) => (
    <nav className='cart-icon' onClick={toggleCartHidden}>
        <ShoppingIcon className='shopping-icon' />
        <span className='item-count'>0</span>
    </nav>
)

const mapDispatchToProps = dispatch => ({
    toggleCartHidden: () => dispatch(toggleCartHiddenAct())
})

const mapStateToProps = ({ cart: { cartItems } }) => ({
    itemCount: cartItems.reduce((accumulatedQuantity, cartItem) => (
        accumulatedQuantity + cartItem.quantity
    ), 0)
})

// 要把所有 item 的 quantity 都加在一起 => reduce(function(計算的值, ) ,acc)
// reduce()     每次計算的結果會再與下個元素作計算，直到結束
// accumulatedQuantity,
// cartItem.quantity   =>  個別 item 的數量

export default connect(null, mapDispatchToProps)(CartIcon);
