import { takeLatest, put, all, call } from 'redux-saga/effects';
import UserActionTypes from './user.types';
import { auth, googleProvider, createUserProfileDocument } from '../../firebase/firebase.utils';


// sign in with google
export function* signInWithGoogle(){
    // SignIn.jsx 裡的 button onClick={signInWithGoogle}
// firebase.utils 方法   export const signInWithGoogle = () => auth.signInWithPopup(provider);
// 因為沒有要用 auth.onAuthStateChanged 所以要把 googleProvider 移進來
// 放入 App.js componentDidMount 的 code
    try{
        // const signInWithGoogle = () => auth.signInWithPopup(provider);  (firebase.utils 方法)
        // 不用 function 是因為要取得 success 回傳的 value
        const userRef = yield auth.signInWithPopup(googleProvider)
        console.log(userRef)
    } catch(error){

    }
}


// 監聽 googleSignInState > 觸發 actual sign form
export function* onGoogleSignInStart(){
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_PENDING, signInWithGoogle)
}

export function* userSagas() {
    yield all([
        call(onGoogleSignInStart)
    ])
}
