import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';

class ShopPage extends Component {
    // collections array snapshot from firestore
    unsubscribeFromSnapshot = null;

    componentDidMount(){
        // QueryReference > collection reference
        const collectionRef = firestore.collection('collections');
        // listen to collectionRef update > get actual data(collections array snapshot)
        // async > 因為 data 在 querySnapshot obj 裡面
        collectionRef.onSnapshot(async snapshot => {
            console.log(snapshot);
            // convertCollectionsSnapshotToMap(snapshot);
        })

    }

    render(){
        const { match } = this.props;
        return(
            <div className='shop-page'>
                <Route exact path={`${match.path}`}>    {/* matching current path */}
                    <CollectionsOverview />
                </Route>
                <Route path={`${match.path}/:collectionId`} component={CollectionPage} />
            </div>
        );
    }
}

export default ShopPage;
