import { takeEvery, call, put } from 'redux-saga/effects';
import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';
import { fetchCollectionsSuccess, fetchCollectionsFailure } from './shop.actions';
import ShopActionTypes from './shop.types';

// query database
export function* fetchCollectionsAsync() {
    yield console.log('fetchCollectionsAsync gen func is fired');

    try{
        // QueryReference > collection reference
        const collectionRef = firestore.collection("collections");
        // QuerySnapshot > collection snapshot
        const snapshot = yield collectionRef.get();
        const collectionMap = yield call(convertCollectionsSnapshotToMap, snapshot);
        yield put(fetchCollectionsSuccess(collectionMap))
    }catch(error){
        yield put(fetchCollectionsFailure(error.message))
    }

    // API call > get actual data(collections array snapshot) from firebase
    // collectionRef
    //     .get()
    //     .then(snapshot => {
    //         // Get collections obj data (converted from array)
    //         const collectionMap = convertCollectionsSnapshotToMap(snapshot);
    //         dispatch(fetchCollectionsSuccess(collectionMap))
    //     })
    //     .catch(error => dispatch(fetchCollectionsFailure(error.message)))
}

// base saga - generator function
export function* fetchCollectionsStart(){
    yield takeEvery(ShopActionTypes.FETCH_COLLECTIONS_PENDING, fetchCollectionsAsync)
}
