// export default ScrollToTop;

function ScrollToTop({ children }) {
    const history = useHistory();
    useEffect(() => {
      const unlisten = history.listen(() => {
        window.scrollTo(0, 0);
      });
      return () => {
        unlisten();
      }
    }, [history]);
  
    return <Fragment>{children}</Fragment>;
  }
  
//   export default withRouter(ScrollToTop);
export default ScrollToTop;



