export const addItemToCart = (cartItems, cartItemToAdd) => {
    const existingCartItem = cartItems.find(
        cartItem => cartItem.id === cartItemToAdd.id
    ) // each

    if(existingCartItem){
        return cartItems.map(cartItem => 
            cartItem.id === cartItemToAdd.id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem    
        )
        // 如果不 map 的話 > 直接回傳 new array，不能確保有更新到 state 影響 re-render
    }
    // not existing
    return [...cartItems, {...cartItemToAdd, quantity: 1}]   // 第一次被加入
}



// cartItems: existing items in array
// cartItemToAdd: new item

// 看 cartItemToAdd 這個商品 是不是已經存在 cartItems 裡了

// find()  依條件 > 如果是 true > 會回傳在 array 裡第一個找到的項目

// true  => 會把這項商品 設成常數
// false => undefined
